import{_ as p,o as e,c as s,e as a}from"./app.3222cd28.js";const t={},r=a('<h1 id="\u6D45\u8C08\u6587\u8A00\u6587\u4E0E\u82F1\u8BED\u8BED\u6CD5\u7684\u76F8\u4F3C\u70B9" tabindex="-1"><a class="header-anchor" href="#\u6D45\u8C08\u6587\u8A00\u6587\u4E0E\u82F1\u8BED\u8BED\u6CD5\u7684\u76F8\u4F3C\u70B9" aria-hidden="true">#</a> \u6D45\u8C08\u6587\u8A00\u6587\u4E0E\u82F1\u8BED\u8BED\u6CD5\u7684\u76F8\u4F3C\u70B9</h1><p><strong>\u6D59\u6C5F\u7701\u5B81\u6CE2\u5E02\u9547\u6D77\u533A\u4EC1\u7231\u4E2D\u5B66 \u65B9\u4E54</strong></p><hr><p>\u200B \u901A\u8FC7\u5BF9**\u300A\u4E49\u52A1\u6559\u80B2\u5168\u65E5\u5236\u521D\u7EA7\u4E2D\u5B66\u8BED\u6587\u6559\u5B66\u6307\u5BFC\u7EB2\u8981\uFF08\u8BD5\u7528\uFF09\u300B**\u7684\u5B66\u4E60,\u6211\u4EEC\u610F\u8BC6\u5230\u5B9E\u65BD\u7D20\u8D28\u6559\u80B2,\u8F6C\u53D8\u6559\u5B66\u89C2\u5FF5\u662F\u9996\u8981\u95EE\u9898\u3002\u8BED\u6587\u6559\u5E08\u4E00\u5B9A\u8981\u628A\u81EA\u5DF1\u7684\u89D2\u8272\u5B9A\u4F4D\u4ECE\u77E5\u8BC6\u7684\u4F20\u6388\u8005\u8F6C\u53D8\u4E3A\u5B66\u6CD5\u7684\u6307\u5BFC\u8005\u3002\u7531\u4E8E\u53D7\u4F20\u7EDF\u7684\u5E94\u8BD5\u6559\u80B2\u7684\u5F71\u54CD\uFF0C\u5F53\u524D\u7EDD\u5927\u591A\u6570\u7684\u6587\u79D1\u79D1\u76EE\u4ECD\u7136\u5145\u65A5\u7740\u6B7B\u8BB0\u786C\u80CC\u73B0\u8C61\uFF0C\u4E2D\u5B66\u9636\u6BB5\u7684\u6587\u8A00\u6587\u548C\u82F1\u8BED\u5C31\u662F\u5982\u6B64\uFF0C\u5E76\u6210\u4E3A\u8BFE\u5802\u6559\u5B66\u4E3B\u6D41\u3002\u6240\u4EE5\u6211\u4EEC\u8981\u610F\u8BC6\u5230\u5F15\u5BFC\u5B66\u751F\u5728\u7406\u89E3\u548C\u5174\u8DA3\u7684\u57FA\u7840\u4E0A\u62D3\u5BBD\u77E5\u8BC6\u9762\u7684\u91CD\u8981\u610F\u4E49\u3002</p><p>\u200B \u6587\u8A00\u6587\u548C\u82F1\u8BED\u90FD\u5C5E\u4E8E\u6587\u79D1\u7C7B\uFF0C\u5374\u90FD\u662F\u4EE4\u79D1\u4EFB\u8001\u5E08\u548C\u5B66\u751F\u5934\u75BC\u7684\u79D1\u76EE\u3002\u4E00\u4E2A\u662F\u53E4\u6C49\u8BED\u6559\u5B66,\u4E00\u4E2A\u5916\u6587\u6559\u5B66,\u770B\u4F3C\u6CA1\u4EC0\u4E48\u8054\u7CFB,\u4F46\u4ED4\u7EC6\u89C2\u5BDF\u4ED6\u4EEC\u7684\u8BED\u6CD5,\u662F\u6709\u76F8\u4F3C\u4E4B\u5904\u7684\uFF0C\u90FD\u5305\u62EC\u5BF9**\u201C\u6587\u201D<strong>\u548C</strong>\u201C\u8A00\u201D**\u7684\u4E24\u65B9\u9762\u8003\u67E5\u3002\u5BF9\u201C\u6587\u201D\u7684\u91CD\u89C6\uFF0C\u4E3B\u8981\u662F\u8003\u8BED\u6BB5\u3001\u6587\u610F\u7684\u7406\u89E3\u80FD\u529B\uFF0C\u53EA\u6709\u4E2A\u522B\u9898\u76EE(\u4E00\u822C\u662F\u6700\u540E\u4E00\u9053\u9898)\u8003\u5206\u6790\u3001\u6982\u62EC\u80FD\u529B\u3002\u800C\u5BF9\u201C\u8A00\u201D\u7684\u91CD\u89C6\uFF0C\u5B9E\u8D28\u4E0A\u662F\u8003\u6587\u8A00\u77E5\u8BC6\u7684\u8FC1\u79FB\u80FD\u529B\uFF0C\u5373\u5229\u7528\u6709\u5173\u6587\u8A00\u77E5\u8BC6\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u7684\u80FD\u529B\uFF0C\u8BED\u6587\u5728\u8FD9\u65B9\u9762\u6BD4\u8F83\u660E\u663E\u3002\u7531\u4E8E\u4FA7\u91CD\u8003\u7406\u89E3\u80FD\u529B\uFF0C\u5C31\u975E\u5E38\u6CE8\u91CD\u8BED\u5883\u56E0\u7D20\uFF0C\u7279\u522B\u662F\u5BF9\u82F1\u8BED\u800C\u8A00\u3002\u540C\u65F6\uFF0C\u7406\u89E3\u8BCD\u8BED\u542B\u4E49\u4E5F\u5FC5\u987B\u8981\u7ED3\u5408\u8BED\u5883\uFF0C\u81F3\u4E8E\u6587\u610F\u6574\u4F53\u628A\u63E1\u65B9\u9762\u7684\u8003\u67E5\uFF0C\u5C31\u66F4\u79BB\u4E0D\u5F00\u539F\u6587\uFF0C\u79BB\u4E0D\u5F00\u8BED\u5883\u4E86\u3002</p><p>\u200B \u6587\u8A00\u6587\u548C\u82F1\u8BED\u7684\u8003\u67E5\uFF0C\u65E0\u8BBA\u662F\u8BCD\u3001\u7279\u6B8A\u53E5\u5F0F\u3001\u6587\u53E5\u7406\u89E3\uFF0C\u8FD8\u662F\u7FFB\u8BD1\u8BED\u6BB5\uFF0C\u65E0\u4E00\u4E0D\u662F\u6E90\u81EA\u4E8E\u8BFE\u5185\uFF0C\u51E0\u4E4E\u5386\u5E74\u7684\u8003\u70B9\u8BBE\u7F6E\uFF0C\u90FD\u53EF\u4EE5\u4ECE\u8BFE\u672C\u4E2D\u627E\u5230\u76F8\u5173\u8054\u7684\u77E5\u8BC6\u70B9\u3002\u53EF\u89C1\uFF0C<strong>\u5B66\u597D\u8BFE\u672C\uFF0C\u5DE9\u56FA\u57FA\u7840\u77E5\u8BC6</strong>\uFF0C\u662F\u5173\u952E\u3002\u90A3\u4E48\uFF0C\u77E5\u8BC6\u5982\u4F55\u5F97\u5230\u6709\u6548\u7684\u5DE9\u56FA\uFF1F\u5982\u4F55\u505A\u5230\u8FC1\u79FB\uFF0C\u8F6C\u5316\u6210\u80FD\u529B\uFF1F\u5F52\u6839\u7ED3\u5E95\uFF0C<strong>\u6CA1\u6709\u91CF\u7684\u79EF\u7D2F\uFF0C\u5C31\u6CA1\u6709\u8D28\u7684\u98DE\u8DC3</strong>\uFF0C\u4E0D\u80FD\u6709\u4E09\u9685\u5728\u80F8\uFF0C\u4FBF\u4E0D\u80FD\u4EE5\u4E00\u9685\u53CD\u4E4B\uFF0C\u53EA\u6709\u57FA\u7840\u624E\u5B9E\uFF0C\u624D\u80FD\u89E6\u7C7B\u65C1\u901A\u3002\u73B0\u6BD4\u8F83\u4E24\u8005\u7684\u4E0B\u9762\u51E0\u4E2A\u8BED\u6CD5\u65B9\u9762\uFF1A<strong>\u4E00\u8BCD\u591A\u4E49\u7684\u73B0\u8C61\u3001\u72B6\u8BED\u540E\u7F6E\u73B0\u8C61\u3001\u5B9A\u8BED\u540E\u7F6E\u73B0\u8C61\u3001\u53CC\u5BBE\u8BED\u73B0\u8C61\u3001\u5224\u65AD\u53E5\u5F0F\u3001\u88AB\u52A8\u53E5\u5F0F\u3001\u7FFB\u8BD1</strong>\u3002</p><h2 id="\u4E00\u3001\u4E00\u8BCD\u591A\u4E49\u7684\u73B0\u8C61" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u4E00\u8BCD\u591A\u4E49\u7684\u73B0\u8C61" aria-hidden="true">#</a> \u4E00\u3001\u4E00\u8BCD\u591A\u4E49\u7684\u73B0\u8C61</h2><p><strong>\u4E00\u8BCD\u591A\u4E49\u7684\u73B0\u8C61\uFF1A\u5C31\u662F\u4E00\u4E2A\uFF08\u5355\uFF09\u8BCD\u6709\u591A\u4E2A\u8BCD\u6027\u548C\u591A\u4E2A\u8BCD\u610F\u3002</strong></p><p>\u5982:\u201C\u4E4B\u201D\uFF1A</p><p>\u200B \u64CD\u86C7\u4E4B\u795E\u95FB\u4E4B\u300A\u611A\u516C\u79FB\u5C71\u300B\u524D\u4E00\u4E2A\u201C\u4E4B\u201D\u52A9\u8BCD\uFF0C\u653E\u5728\u4FEE\u9970\u8BED\u548C\u4E2D\u5FC3\u8BED\u4E4B\u95F4\uFF0C\u610F\u601D\u662F\u201C\u7684\u201D\uFF1B\u540E\u4E00\u4E2A\u201C\u4E4B\u201D\u662F\u4EE3\u8BCD\uFF0C\u4EE3\u611A\u516C\u79FB\u5C71\u8FD9\u4EF6\u4E8B\u3002</p><p>\u200B \u543E\u6B32\u4E4B\u5357\u6D77\u300A\u4E3A\u5B66\u300B\u52A8\u8BCD\uFF0C\u610F\u601D\u662F\u201C\u5230\uFF0C\u5F80\u201D\u3002</p><p>\u200B \u5974\u5FC3\u4E4B\u56FA\uFF0C\u56FA\u4E0D\u53EF\u5F7B\u300A\u611A\u516C\u79FB\u5C71\u300B\u52A9\u8BCD\uFF0C\u653E\u5728\u4E3B\u8BED\u548C\u8C13\u8BED\u4E4B\u95F4\uFF0C\u53EF\u4E0D\u8BD1\u3002</p><p>\u200B \u4E4B\u5B50\u4E8E\u5F52\u300A\u8BD7\u7ECF\xB7\u5468\u5357\xB7\u6843\u592D\u300B\u6307\u793A\u4EE3\u8BCD\uFF0C\u8FD9\uFF0C\u6B64\u7684\u610F\u601D\u3002</p><p>\u200B \u5B66\u800C\u65F6\u4E60\u4E4B\u300A\u8BBA\u8BED\xB7\u5B66\u800C\u300B\u7B2C\u4E09\u4EBA\u79F0\u4EE3\u8BCD\uFF0C\u5B83\u7684\u610F\u601D\u3002</p><p>\u518D\u5982\u201C\u4F7F\u201D\uFF1A</p><p>\u200B \u4F7F\u5B50\u4E3A\u4F7F\u300A\u664F\u5B50\u6625\u79CB\u300B\u524D\u4E00\u4E2A\u201C\u4F7F\u201D\u662F\u52A8\u8BCD\uFF0C\u6D3E\u9063\u7684\u610F\u601D\u3002\u540E\u4E00\u4E2A\u201C\u4F7F\u201D\u662F\u540D\u8BCD\uFF0C\u4F7F\u8005\u7684\u610F\u601D\u3002</p><p>\u200B \u4F7F\u6B66\u5B89\u4FAF\u5728\u8005\uFF0C\u65CF\u77E3\uFF01\u300A\u53F2\u8BB0\xB7\u9B4F\u5176\u6B66\u5B89\u4FAF\u5217\u4F20\u300B\u4F5C\u8FDE\u8BCD\uFF0C\u5047\u4F7F\uFF0C\u5047\u82E5\u7684\u610F\u601D\u3002</p><p>\u200B \u4E43\u9063\u975E\u4F7F\u79E6\u300A\u53F2\u8BB0\xB7\u97E9\u975E\u4F20\u300B\u52A8\u8BCD\uFF0C\u662F\u51FA\u4F7F\u7684\u610F\u601D\u3002</p><p>\u200B \u800C\u82F1\u8BED\u5728\u4E00\u8BCD\u591A\u4E49\u4E0A\u4E5F\u6709\u76F8\u540C\u7684\u8BED\u6CD5\u70B9\uFF0C\u4E5F\u662F\u82F1\u8BED\u5355\u8BCD\u7684\u57FA\u672C\u7279\u5F81\u3002\u5982\uFF1A</p><p>\u200B fake\u3014\u540D\u8BCD\u3015\u5192\u724C\u8D27\uFF1B\u4F2A\u9020\u54C1\uFF1B\u9A97\u5B50\u3014\u52A8\u8BCD\u3015\u4F2A\u9020\uFF1B\u4F2A\u88C5\u3014\u5F62\u5BB9\u8BCD\u3015\u5047\u7684\uFF1B\u4F2A\u9020\u7684</p><p>\u200B like\u3014\u53CA\u7269\u52A8\u8BCD\u3015\u559C\u6B22\u3014\u4ECB\u8BCD\u3015\u50CF\uFF1B\u8DDF\u2026\u4E00\u6837\u3014\u540D\u3015\u55DC\u597D\u3014\u5F62\u5BB9\u8BCD\u3015\u76F8\u4F3C\u7684 \u200B \u3014\u526F\u8BCD\u3015\u540C\u6837\u5730\u3014\u8FDE\u8BCD\u3015\u597D\u50CF\uFF1B\u5982\u540C</p><p>\u200B book\u3014\u540D\u8BCD\u3015\u4E66\uFF1B\u518C\uFF1B\u5377\u3014\u53CA\u7269\u52A8\u8BCD\u3015\u767B\u8BB0\uFF1B\u6258\u8FD0\uFF1B\u63A7\u544A\uFF1B\u6CE8\u518C\u3014\u4E0D\u53CA\u7269\u52A8\u8BCD\u3015\u8BA2\u5EA7</p><p>\u200B \u82F1\u8BED\u548C\u6587\u8A00\u6587\u5728\u53E5\u5B50\u4E2D\u4F1A\u968F\u7740\u6587\u7AE0\u548C\u53E5\u5B50\u7684\u5177\u4F53\u610F\u601D\u800C\u4E0D\u65AD\u53D8\u5316\u7684\u7279\u5B9A\u8BED\u5883\u4E2D\u7684\u8BCD\u8BED\u5F80\u5F80\u6709\u7279\u5B9A\u7684\u542B\u4E49\u3002\u5C24\u5176\u662F\u6587\u8A00\u5B9E\u8BCD\uFF0C\u7531\u4E8E\u5176\u5177\u6709\u591A\u4E49\u6027\uFF0C\u79BB\u5F00\u8BED\u5883\u53EF\u80FD\u4F1A\u505A\u51FA\u5B8C\u5168\u9519\u8BEF\u7684\u5224\u65AD\u3002\u90A3\u4E48\uFF0C\u7406\u89E3\u8BCD\u8BED\u542B\u4E49\u5C31\u5FC5\u987B\u7ED3\u5408\u8BED\u5883\u4E86\u3002\u81F3\u4E8E\u6587\u610F\u6574\u4F53\u628A\u63E1\u65B9\u9762\u7684\u8003\u67E5\uFF0C\u5C31\u66F4\u79BB\u4E0D\u5F00\u539F\u6587\uFF0C\u79BB\u4E0D\u5F00\u8BED\u5883\u3002\u6240\u4EE5\u6211\u4EEC\u8981\u5168\u9762\u800C\u5177\u4F53\u7684\u628A\u63E1\u6BCF\u4E00\u4E2A\u8BCD\u7684\u8BCD\u6027\u548C\u8BCD\u4E49\uFF0C\u624D\u80FD\u5728\u9605\u8BFB\u548C\u89E3\u9898\u4E2D\u505A\u5230\u6E38\u5203\u6709\u4F59\uFF0C\u5F04\u61C2\u6587\u610F\u548C\u53E5\u4E49\uFF0C\u624D\u80FD\u5728\u8003\u8BD5\u65F6\u4E0D\u56E0\u4E3A\u8BCD\u4E49\u7684\u95EE\u9898\u800C\u8F7B\u6613\u5931\u5206\uFF0C\u5728\u9605\u8BFB\u65F6\u80FD\u591F\u6E05\u695A\u7684\u4E86\u89E3\u6574\u4F53\u7684\u610F\u601D\u3002</p><h2 id="\u4E8C\u3001\u72B6\u8BED\u540E\u7F6E\u73B0\u8C61" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u72B6\u8BED\u540E\u7F6E\u73B0\u8C61" aria-hidden="true">#</a> \u4E8C\u3001\u72B6\u8BED\u540E\u7F6E\u73B0\u8C61\uFF1A</h2><p>\u200B <strong>\u5728\u73B0\u4EE3\u6C49\u8BED\u53E5\u5B50\u6210\u5206\u4E2D\uFF0C\u526F\u8BCD\u548C\u4ECB\u8BCD\u77ED\u8BED\u4F5C\u72B6\u8BED\u4FEE\u9970\u8C13\u8BED\uFF0C\u5E94\u653E\u5728\u8C13\u8BED\u7684\u524D\u9762\uFF0C\u82E5\u7F6E\u4E8E\u8C13\u8BED\u4E4B\u540E\u4FBF\u662F\u8865\u8BED\u3002\u800C\u5728\u6587\u8A00\u6587\u91CC\uFF0C\u5904\u4E8E\u8865\u8BED\u7684\u6210\u5206\u5F80\u5F80\u8981\u4EE5\u72B6\u8BED\u6765\u7406\u89E3\uFF0C\u65E2\u72B6\u8BED\u653E\u5728\u8C13\u8BED\u7684\u540E\u9762\uFF0C\u5F62\u6210\u5012\u88C5\u53E5\uFF0C\u6211\u4EEC\u628A\u8FD9\u79CD\u8BED\u6CD5\u73B0\u8C61\u5C31\u53EB\u72B6\u8BED\u540E\u7F6E\u3002</strong></p><p>\u200B \u4F8B\u5982\uFF1A\u201C\u4E3A\u5C0F\u95E8\u4E8E\u5927\u95E8\u4E4B\u4FA7\u800C\u5EF6\u664F\u5B50\u201D\u300A\u664F\u5B50\u6625\u79CB\u300B\u5982\u679C\u6309\u7167\u6587\u8A00\u548C\u82F1\u8BED\u7684\u8BD1\u6CD5\u662F\uFF1A\u5F00\u4E00\u6247\u5C0F\u95E8\u5728\u5927\u95E8\u8FB9\u8BF7\u664F\u5B50\u8FDB\u53BB\u3002\u8FD9\u6837\u76F4\u8BD1\u5C31\u6709\u70B9\u62D7\u53E3\u4E86\uFF0C\u800C\u4E14\u8BED\u5E8F\u4E0D\u901A\u3002\u6B63\u786E\u7684\u8BED\u5E8F\u5E94\u662F\uFF1A\u4E8E\u5927\u95E8\u4E4B\u4FA7\u4E3A\u5C0F\u95E8\u800C\u5EF6\u664F\u5B50\uFF08\u5728\u5927\u95E8\u65C1\u8FB9\u5F00\u4E86\u4E00\u6247\u5C0F\u95E8\u8BF7\u664F\u5B50\u8FDB\u53BB\uFF09\u3002</p><p>\u200B \u201C\u97E9\u544A\u6025\u4E8E\u9F50\u201D\u300A\u53F2\u8BB0\xB7\u5B59\u5B50\u5434\u8D77\u5217\u4F20\u300B\u8FD9\u4E5F\u662F\u72B6\u8BED\u540E\u7F6E\u53E5\u3002\u5E94\u4E3A\u201C\u97E9\u4E8E\u9F50\u544A\u6025\u201D\uFF08\u97E9\u56FD\u5411\u9F50\u56FD\u544A\u6025\u6C42\u63F4\uFF09\u3002</p><p>\u200B \u5728\u82F1\u8BED\u91CC\u4E5F\u6709\u90E8\u5206\u526F\u8BCD\u4FEE\u9970\u52A8\u8BCD\u6216\u77ED\u8BED\u65F6\uFF0C\u4E5F\u51FA\u73B0\u72B6\u8BED\u540E\u7F6E\u7684\u73B0\u8C61\u4F4D\u7F6E\u4E5F\u5728 \u8C13\u8BED\u7684\u540E\u9762\u3002\u4F8B\u5B50\uFF1A</p><p>\u200B She runs so fast!\uFF08\u5979\u8DD1\u5F97\u591A\u5FEB\u554A\uFF01\uFF09\u201C\u591A\u5FEB\uFF08so fast\uFF09\u201D\u4F5C\u72B6\u8BED\u4FEE\u9970\u201C\u8DD1(runs)\u201D\u3002</p><p>\u200B Tom returned home at 5 o\u2019clock precisely\u3002\uFF08\u6C64\u59C6\u6B635\u70B9\u56DE\u5BB6\uFF09\u201Cprecisely\u201D\u4FEE\u9970\u77ED\u8BED\u201C5 o\u2019clock\u201D\u3002</p><p>\u200B You should not work so hard ,especially after you have been ill\u3002\uFF08\u4F60\u4E0D\u5E94\u8BE5\u8FD9\u6837\u7528\u529F\uFF0C\u7279\u522B\u662F\u5728\u4F60\u751F\u75C5\u4E4B\u540E\u3002\uFF09\u201Cespecially\u201D\u4FEE\u9970after\u4ECE\u53E5\u3002</p><p>\u200B \u82F1\u8BED\u548C\u6587\u8A00\u6587\u5728\u8FD9\u4E2A\u8BED\u6CD5\u4E0A\u662F\u5F88\u76F8\u4F3C\u7684\u3002\u72B6\u8BED\u5C31\u653E\u5728\u8C13\u8BED\u5F97\u540E\u9762\uFF0C\u5C5E\u6B63\u5E38\u8BED\u5E8F\uFF0C\u800C\u5728\u73B0\u4EE3\u6C49\u8BED\u8BED\u6CD5\u91CC\u5C31\u5C5E\u4E8E\u72B6\u8BED\u540E\u7F6E\u73B0\u8C61\u4E86\u3002</p><h2 id="\u4E09\u3001\u5B9A\u8BED\u540E\u7F6E\u73B0\u8C61" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u5B9A\u8BED\u540E\u7F6E\u73B0\u8C61" aria-hidden="true">#</a> \u4E09\u3001\u5B9A\u8BED\u540E\u7F6E\u73B0\u8C61\uFF1A</h2><p>\u200B <strong>\u5728\u6587\u8A00\u548C\u82F1\u8BED\u8BED\u6CD5\u4E2D\uFF0C\u5B9A\u8BED\u4F5C\u4FEE\u9970\u8BED\u653E\u5728\u4E2D\u5FC3\u8BCD\u540E\u9762\u3002</strong></p><p>\u200B \u4F8B\u5982\uFF1A\u201C\u5C45\u5E99\u5802\u4E4B\u9AD8\u201D\u300A\u5CB3\u9633\u697C\u8BB0\u300B\uFF0C\u201C\u9AD8\u201D\u662F\u5F62\u5BB9\u8BCD\uFF0C\u7528\u6765\u4FEE\u9970\u201C\u5E99\u5802\u201D\uFF0C\u5E94\u7406\u89E3\u4E3A\u201C\u9AD8\u9AD8\u7684\u5E99\u5802\u201D\u3002\u518D\u5982\u201C\u9042\u7387\u5B50\u5B59\u8377\u62C5\u8005\u4E09\u592B\u201D\u300A\u611A\u516C\u79FB\u5C71\u300B\uFF0C\u5E94\u8BD1\u4E3A\u201C\u9042\u7387\u8377\u62C5\u4E4B\u5B50\u5B59\u4E09\u592B\u201D\u3002\u201C\u8377\u62C5\u201D\u4F5C\u5B9A\u8BED\u4FEE\u9970\u201C\u5B50\u5B59\u4E09\u592B\u201D\uFF0C\u7FFB\u8BD1\u4E3A\uFF1A\u4E8E\u662F\u7387\u9886\u5B50\u5B59 \u5F53\u4E2D\u80FD\u6311\u62C5\u7684\u4E09\u4E2A\u4EBA\uFF08\u4E0A\u4E86\u5C71\uFF09\u3002</p><p>\u82F1\u8BED\u7684\u4F8B\u5B50\u4E3A\uFF1A</p><p>\u200B The desks in the classroom are 25 , but the desks outside are only 5\u3002\u90A3\u4E48\u201Cin the classroom \u201D\u5C31\u662F\u4ECB\u8BCD\u77ED\u8BED\u4F5C\u5B9A\u8BED\u4FEE\u9970\u201Cthe desks\u201D\uFF0C\u7FFB\u8BD1\u4E3A\u201C\u6559\u5BA4\u91CC\u7684\u8BFE\u684C\u201D\uFF0C\u201Coutside\u201D\u4E5F\u662F\u4F5C\u5B9A\u8BED\u4FEE\u9970\u201Cthe desks \u201D\u8BD1\u4E3A\u201C\u6559\u5BA4\u5916\u7684\u8BFE\u684C\u201D\u3002</p><h2 id="\u56DB\u3001\u53CC\u5BBE\u8BED\u73B0\u8C61" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u53CC\u5BBE\u8BED\u73B0\u8C61" aria-hidden="true">#</a> \u56DB\u3001\u53CC\u5BBE\u8BED\u73B0\u8C61\uFF1A</h2><p>\u200B <strong>\u4E00\u4E2A\u52A8\u8BCD\u6709\u65F6\u5E26\u4E24\u4E2A\u5BBE\u8BED\u3002\u53CC\u5BBE\u8BED\u4E2D\u5F80\u5F80\u6709\u4E00\u4E2A\u5BBE\u8BED\u6307\u4EBA\uFF0C\u53E6\u4E00\u4E2A\u5BBE\u8BED\u6307\u7269\u3002</strong></p><p>\u200B \u5728\u6587\u8A00\u6587\u91CC\uFF0C\u5E26\u53CC\u5BBE\u8BED\u7684\u52A8\u8BCD\uFF0C\u5E38\u89C1\u7684\u6709\u201C\u4E88\u3001\u8D50\u3001\u9057\uFF08w\xE8i\uFF09\u3001\u6559\u3001\u8BED\u3001\u544A\u3001\u95EE\u201D\u7B49\u3002\u4F8B\u5982\u300A\u5EC9\u9887\u853A\u76F8\u5982\u5217\u4F20\u300B\uFF1A\u201C\u4F7F\u4EBA\u9057\u8D75\u738B\u4E66\u3002\u201D\uFF08\u6D3E\u4EBA\u7ED9\u8D75\u738B\u4E00\u5C01\u4FE1\u3002\uFF09\u52A8\u8BCD\u201C\u9057\u201D\u5E26\u6709\u201C\u8D75\u738B\u201D\u548C\u201C\u4E66\u201D\u4E24\u4E2A\u5BBE\u8BED\uFF0C\u524D\u4E00\u4E2A\u5BBE\u8BED\u201C\u8D75\u738B\u201D\u6307 \u4EBA\uFF0C\u540E\u4E00\u4E2A\u5BBE\u8BED\u201C\u4E66\u201D\u6307\u7269\u3002\u53C8\u5982\u300A\u516C\u8F93\u300B\uFF1A\u201C\u543E\u65E2\u5DF2\u8A00\u4E4B\u738B\u77E3\u3002\u201D\uFF08\u6211\u5DF2\u7ECF\u628A\u8FD9\u4EF6\u4E8B\u544A\u8BC9\u695A\u738B\u4E86\u3002\uFF09\u52A8\u8BCD\u201C\u8A00\u201D\u5E26\u6709\u201C\u4E4B\u201D\u548C\u201C\u738B\u201D\u4E24\u4E2A\u5BBE\u8BED\uFF0C\u524D\u4E00\u4E2A\u5BBE\u8BED\u201C\u4E4B\u201D\u6307\u4E8B\u7269\uFF0C\u540E\u4E00\u4E2A\u5BBE\u8BED\u201C\u738B\u201D\u6307\u4EBA\u3002</p><p>\u200B \u82F1\u8BED\u4E2D\u5E26\u53CC\u5BBE\u8BED\u7684\u52A8\u8BCD\u4E00\u822C\u662F\u53CA\u7269\u52A8\u8BCD\uFF0C\u5E38\u89C1\u7684\u6709\u201Cgive\u3001pass \u3001offer\u3001teach\u3001show\u2026\u201D\u4F8B\u5982\u201Cgive sb. sth.\u201D\u7FFB\u8BD1\u4E3A\u201C\u7ED9\u67D0\u4EBA\u67D0\u4E1C\u897F\u201D\uFF0C\u8FD9\u91CC\u7684\u52A8\u8BCD\u201Cgive\u201D\u540E\u9762\u5E26\u6709\u201Csb.\u201D\u548C\u201Csth.\u201D\u4E24\u4E2A\u5BBE\u8BED\uFF0C\u524D\u4E00\u4E2A\u201Csb.\u201D\u5373\u201Csomebody\u201D\uFF08\u67D0\u4EBA\uFF09\uFF0C\u540E\u4E00\u4E2A\u201Csth.\u201D\u610F\u4E3A \u201Csomething\u201D\uFF08\u67D0\u4E8B\uFF09\u3002</p><p>\u200B pass sb. sth. \u9012\u7ED9\u67D0\u4EBA\u67D0\u4E1C\u897F \u200B offer sb. sth. \u63D0\u4F9B\u67D0\u4EBA\u67D0\u4E1C\u897F \u200B teach sb.sth. \u6559\u67D0\u4EBA\u67D0\u4E1C\u897F \u200B show sb. sth. \u5C55\u793A\u67D0\u4EBA\u67D0\u4E1C\u897F</p><p>\u200B \u4E0A\u9762\u7684\u8FD9\u4E9B\u52A8\u8BCD\u90FD\u548C\u201Cgive sb. sth.\u201D\u7684\u8BED\u6CD5\u662F\u4E00\u6837\u7684\uFF0C\u90FD\u63A5\u53CC\u5BBE\u8BED\uFF0C\u524D\u4E00\u4E2A\u63A5\u4EBA\uFF0C\u540E\u4E00\u4E2A\u63A5\u7269\u3002</p><h2 id="\u4E94\u3001\u5224\u65AD\u53E5\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001\u5224\u65AD\u53E5\u5F0F" aria-hidden="true">#</a> \u4E94\u3001\u5224\u65AD\u53E5\u5F0F\uFF1A</h2><p>\u200B <strong>\u5BF9\u4E3B\u8BED\u6709\u6240\u5224\u65AD\u7684\u53E5\u5B50\u3002\u6709\u65F6\uFF0C\u5224\u65AD\u53E5\u7684\u4E3B\u8BED\u548C\u8C13\u8BED\u6240\u6307\u7684\u662F\u540C\u4E00\u4E8B\u7269\u3002\u6709\u65F6\uFF0C\u8C13\u8BED\u53EA\u662F\u65AD\u5B9A\u6240\u6307\u7684\u4EBA\u6216\u4E8B\u7269\u5177\u6709\u67D0\u79CD\u6027\u8D28\uFF0C\u6216\u5C5E\u4E8E\u67D0\u4E00\u5927\u7C7B\u3002</strong></p><p>\u200B \u4E0A\u53E4\u6C49\u8BED\u201C\u662F\u201D\u662F\u6307\u793A\u4EE3\u8BCD\uFF0C\u4E00\u822C\u4E0D\u7528\u6765\u6784\u6210\u5224\u65AD\u53E5\u3002\u5224\u65AD\u53E5\u7684\u57FA\u672C\u5F62\u5F0F\u662F\uFF1B\u53E5\u4E2D\u7528\u201C\u8005\u201D\uFF0C\u53E5\u672B\u7528\u201C\u4E5F\u201D\u3002\u5B83\u7684\u53D8\u5316\u5F62\u5F0F\u6709\uFF1A\u53E5\u4E2D\u7528\u201C\u8005\u201D\uFF0C\u53E5\u672B\u4E0D\u7528\u201C\u4E5F\u201D\uFF1B\u53E5\u4E2D\u4E0D\u7528\u201C\u8005\u201D\uFF0C\u53E5\u672B\u7528\u201C\u4E5F\u201D\uFF1B\u53E5\u4E2D\u4E0D\u7528\u201C\u8005\u201D\uFF0C\u53E5\u672B\u4E5F\u4E0D\u7528\u201C\u4E5F\u201D\u3002</p><p>\u200B \u4F8B\u5982\uFF1A\u9648\u80DC\u8005\uFF0C\u9633\u57CE\u4EBA\u4E5F\u3002\uFF08\u300A\u9648\u6D89\u4E16\u5BB6\u300B\uFF09\u2014\u2014\u9648\u80DC\u662F\u9633\u57CE\u4EBA\u3002</p><p>\u200B \u5176\u5DEB\uFF0C\u8001\u5973\u5B50\u4E5F\u3002\uFF08\u300A\u897F\u95E8\u8C79\u6CBB\u90BA\u300B\uFF09\u2014\u2014\u90A3\u4E2A\u5DEB\u5E08\uFF0C\u662F\u4E2A\u8001\u5987\u4EBA\u3002</p><p>\u200B \u592B\u9C81\uFF0C\u9F50\u664B\u4E4B\u5507\u3002\uFF08\u300A\u5DE6\u4F20\xB7\u54C0\u516C\u516B\u5E74\u300B\uFF09\u2014\u2014\u9C81\u56FD\u662F\u9F50\u664B\u4E24\u56FD\u7684\u95E8\u6237\u3002</p><p>\u200B \u4E2D\u53E4\u4EE5\u6765\uFF0C\u201C\u662F\u201D\u7528\u4F5C\u5224\u65AD\u8BCD\uFF0C\u5224\u65AD\u53E5\u8FDB\u4E00\u6B65\u6446\u8131\u4E86\u8BED\u6C14\u8BCD\u201C\u4E5F\u201D\u3002\u5982\u201C\u8C6B\u7AE0\u592A\u5B88\u987E\u90B5\u662F\u96CD\u4E4B\u5B50\u201D\uFF08\u300A\u4E16\u8BF4\u65B0\u8BED\xB7\u96C5\u91CF\u300B\uFF09\u3002</p><h2 id="\u516D\u3001\u88AB\u52A8\u53E5\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u516D\u3001\u88AB\u52A8\u53E5\u5F0F" aria-hidden="true">#</a> \u516D\u3001\u88AB\u52A8\u53E5\u5F0F\uFF1A</h2><p>\u200B <strong>\u4E3B\u8BED\u53D7\u4E8B\uFF0C\u7528\u201C\u88AB\u201D\u5F15\u8FDB\u65BD\u4E8B\uFF0C\u6216\u5C06\u201C\u88AB\u201D\u76F4\u63A5\u9644\u4E8E\u8C13\u8BED\u524D\u8868\u88AB\u52A8\u3002</strong></p><p>\u200B \u73B0\u4EE3\u6C49\u8BED\u88AB\u52A8\u53E5\u53EB\u201C\u88AB\u5B57\u53E5\u201D\u3002\u6587\u8A00\u548C\u82F1\u8BED\u7684\u88AB\u52A8\u53E5\u6709\u4E9B\u533A\u522B\uFF0C\u4F46\u8BED\u6CD5\u548C\u610F\u4E49\u4E0A\u5374\u662F\u76F8\u540C\u6216\u76F8\u8FD1\u7684\u3002 \u6587\u8A00\u6587\u4F8B\u5982\uFF1A</p><p>\u200B \u738B\u5EFA\u79BD\u4E8E\u79E6\uFF08\u300A\u76D0\u94C1\u8BBA\xB7\u8BBA\u5112\u300B\uFF09\u2014\u2014\u9F50\u738B\u7530\u5EFA\u88AB\u79E6\u519B\u64D2\u83B7</p><p>\u200B \u4E0D\u542C\u543E\u8A00\uFF0C\u8EAB\u6B7B\uFF0C\u8EAB\u5B50\u4E3A\u622E\u3002\uFF08\u300A\u56FD\u8BED\xB7\u8D8A\u8BED\u300B\uFF09\u2014\u2014\u4F60\u82E5\u4E0D\u542C\u6211\u7684\u8BDD\uFF0C\u4F60\u8981\u5904\u6B7B\uFF0C\u8001\u5A46\u5B69\u5B50\u4E5F\u8981\u88AB\u5904\u6B7B\u3002</p><p>\u200B \u89C1\u7559\u4E8C\u5341\u5E74\u4E0D\u964D\uFF08\u300A\u6C49\u4E66\xB7\u71D5\u523A\u738B\u4F20\u300B\uFF09\u2014\u2014\uFF08\u82CF\u6B66\uFF09\u88AB\u5308\u5974\u62D8\u7981\u4E86\u4E8C\u5341\u5E74\uFF0C\u4E0D\u6295\u964D\u3002</p><p>\u200B \u4E3A\u5F20\u8FBD\u6240\u88AD\uFF08\u300A\u4E09\u56FD\u5FD7\xB7\u5434\u4E66\xB7\u5415\u8499\u4F20\u300B\uFF09\u2014\u2014\u88AB\u5F20\u8FBD\u5077\u88AD\u3002</p><p>\u200B \u536B\u592A\u5B50\u4E3A\u6C5F\u51B2\u6240\u8D25\u3002\uFF08\u300A\u6C49\u4E66\xB7\u970D\u5149\u4F20\u300B\uFF09\u2014\u2014\u536B\u592A\u5B50\u88AB\u6C5F\u51B2\u9677\u5BB3\u3002</p><p>\u200B \u5728\u82F1\u8BED\u4E2D\uFF0C\u88AB\u52A8\u53E5\u7684\u7528\u6CD5\u79F0\u4E3A\u88AB\u52A8\u8BED\u6001\u3002\u5373\u7531\u52A9\u52A8\u8BCDbe\uFF08am\u3001is\u3001are\uFF3Cwas \u3001were\uFF09\u52A0\u4E0A\u53CA\u7269\u52A8\u8BCD\u7684\u8FC7\u53BB\u5206\u8BCD\u6784\u6210\u3002\u88AB\u52A8\u8BED\u6001\u53EF\u4EE5\u7528\u4E8E\u5404\u79CD\u65F6\u6001\uFF0C\u4F46\u8F83\u5E38\u7528\u7684\u6709\u5341\u79CD\uFF0C\u5728\u8FD9\u91CC\u53EA\u4E3E\u73B0\u5728\u3001\u8FC7\u53BB\u548C\u5C06\u6765\u4E00\u822C\u65F6\u7684\u4F8B\u5B50\u3002\u5982\uFF1A</p><p>\u200B I am not so easily deceived\u3002(\u6211\u4E0D\u662F\u8F7B\u6613\u4E0A\u5F53\u53D7\u9A97\u7684\u3002)</p><p>\u200B Our house was built in 2000\u3002\uFF08\u6211\u4EEC\u5BB6\u7684\u623F\u5B50\u5EFA\u4E8E2000\u5E74\u3002\uFF09</p><p>\u200B This matter will be looked into in the future\u3002\uFF08\u8FD9\u4EF6\u4E8B\u5C06\u6765\u662F\u8981\u67E5\u660E\u7684\u3002\uFF09</p><h2 id="\u4E03\u3001\u7FFB\u8BD1" tabindex="-1"><a class="header-anchor" href="#\u4E03\u3001\u7FFB\u8BD1" aria-hidden="true">#</a> \u4E03\u3001\u7FFB\u8BD1\uFF1A</h2><p>\u200B <strong>\u6B63\u786E\u628A\u63E1\u53E5\u5B50\u5728\u6587\u4E2D\u7684\u610F\u601D\uFF0C\u8FD9\u662F\u6587\u8A00\u6587\u548C\u82F1\u8BED\u9605\u8BFB\u8003\u67E5\u7684\u4E00\u4E2A\u81F3\u5173\u91CD\u8981\u7684\u5185\u5BB9\u3002\u51C6\u786E\u7FFB\u8BD1\u6587\u4E2D\u7684\u53E5\u5B50\uFF0C\u5373\u7528\u73B0\u4EE3\u6C49\u8BED\u7684\u8BCD\u6C47\u548C\u8BED\u6CD5\u6765\u7FFB\u8BD1\u6240\u63D0\u4F9B\u7684\u8BED\u53E5\uFF0C\u505A\u5230\u6587\u901A\u5B57\u987A\uFF0C\u7B80\u660E\u89C4\u8303\uFF0C\u7545\u8FBE\u6D41\u5229\u3002\u6709\u4E00\u70B9\u5E94\u8BE5\u660E\u786E\uFF0C\u6211\u4EEC\u5E94\u628A&quot;\u53D6\u6CD5\u4E8E\u8BFE\u5185\uFF0C\u5DE9\u56FA\u79EF\u7D2F\u4E8E\u8BFE\u5916&quot; \u89C6\u4E3A\u590D\u4E60\u5907\u8003\u7684\u6700\u57FA\u672C\u539F\u5219\u3002</strong></p><p>\u6B63\u786E\u628A\u63E1\u53E5\u5B50\u5728\u6587\u4E2D\u7684\u610F\u601D\uFF0C\u5173\u952E\u662F\u6CE8\u610F\u4EE5\u4E0B\u56DB\u70B9\uFF1A</p><p>\u200B 1\uFF0E\u51C6\u786E\u7406\u89E3\u5E38\u89C1\u6587\u8A00\u548C\u82F1\u8BED\u8BCD\u8BED\u7684\u610F\u4E49\u53CA\u7528\u6CD5\uFF0C\u4ECE\u6587\u7AE0\u6574\u4F53\u53CA\u5177\u4F53\u8BED\u5883\u4E24\u4E2A\u65B9\u9762\u5F04\u61C2\u5B9E\u8BCD\u7684\u610F\u4E49\uFF0C\u4E86\u89E3\u4E0E\u73B0\u4EE3\u6C49\u8BED\u4E0D\u540C\u7684\u53E5\u5F0F\u3002</p><p>\u200B 2\uFF0E\u5F04\u61C2\u590D\u53E5\u4E2D\u5206\u53E5\u4E4B\u95F4\u7684\u8BED\u610F\u5173\u7CFB\u3002</p><p>\u200B 3\uFF0E\u6293\u4F4F\u5173\u952E\u8BED\u53E5\uFF0C\u628A\u63E1\u4E2D\u5FC3\u53E5\uFF0C\u5F04\u61C2\u8574\u542B\u4E30\u5BCC\u8BED\u53E5\u7684\u8868\u5C42\u4E0E\u6DF1\u5C42\u542B\u4E49\u3002</p><p>\u200B 4\uFF0E\u6CE8\u610F\u53E5\u5B50\u7684\u6BD4\u8F83\u5206\u6790\uFF0C\u6CE8\u610F\u53E5\u4E0E\u53E5\u4E4B\u95F4\u7684\u5185\u5728\u8054\u7CFB\uFF0C\u5206\u6790\u5176\u610F\u4E49\u4E0A\u7684\u5DEE\u522B\u3002</p><p>\u200B \u6211\u4EEC\u77E5\u9053\u7FFB\u8BD1\u7684\u57FA\u672C\u8981\u6C42\u662F&quot;\u4FE1&quot;\u3001&quot;\u8FBE&quot;\u3001&quot;\u96C5&quot;\u3002\u5373\uFF1A\u9996\u5148\u8981\u6C42\u51C6\u786E\u8868\u8FBE\u539F\u6587\u610F\u601D\uFF0C\u4E0D\u8D70\u6837\uFF0C\u4E0D\u6F0F\u8BD1\uFF0C\u4E0D\u9519\u8BD1;\u7EE7\u4E4B\u8981\u6C42\u8BD1\u6587\u660E\u767D\u901A\u7545\uFF0C\u65E0\u8BED\u75C5;\u8FDB\u800C\u8981\u6C42\u8BD1\u6587\u7528\u8BCD\u9020\u53E5\u8003\u7A76\uFF0C\u6709\u4E00\u5B9A\u7684\u6587\u91C7\u3002\u5B83\u5C31\u662F\u6211\u4EEC\u5E73\u65F6\u6240\u8BF4\u7684\u4EE5\u76F4\u8BD1\u4E3A\u4E3B\uFF0C\u4EE5\u610F\u8BD1\u4E3A\u8F85,\u5173\u952E\u8981\u786E\u5207\u5730\u8868\u8FBE\u539F\u6587\u539F\u610F\u3002\u82F1\u8BED\u4E5F\u662F\u5982\u6B64\u3002\u7406\u89E3\u8BCD\u8BED\u542B\u4E49\u90FD\u5FC5\u987B\u7ED3\u5408\u8BED\u5883\uFF0C\u81F3\u4E8E\u6587\u610F\u6574\u4F53\u628A\u63E1\u65B9\u9762\u7684\u8003\u67E5\uFF0C\u5C31\u66F4\u79BB\u4E0D\u5F00\u539F\u6587\uFF0C\u79BB\u4E0D\u5F00\u8BED\u5883\u3002</p><p>\u200B \u4ECE\u4E0A\u9762\u7684\u6587\u8A00\u548C\u82F1\u8BED\u6BD4\u8F83\u7684\u4F8B\u5B50\u6211\u4EEC\u53EF\u4EE5\u770B\u51FA\uFF0C\u82F1\u8BED\u5728\u8FD9\u4E9B\u8BED\u6CD5\u548C\u95EE\u9898\u4E0A\u548C\u6587\u8A00\u6587\u6709\u7740\u6781\u5176\u76F8\u8FD1\u7684\u5730\u65B9\u3002\u6240\u4EE5\u6709\u5FC5\u8981\u901A\u8FC7\u4E24\u8005\u7684\u6BD4\u8F83\u5B66\u4E60\uFF0C\u201C\u6709\u6BD4\u8F83\u624D\u6709\u9274\u522B\u201D\uFF0C\u901A\u8FC7\u4E24\u8005\u7684\u6BD4\u8F83,\u53EF\u4EE5\u4ECE\u4E2D\u5F97\u5230\u6559\u4E0E\u5B66\u7684\u4E24\u76F8\u6536\u83B7\uFF0C\u7279\u522B\u662F\u5BF9\u5B66\u751F\u800C\u8A00\uFF0C\u5B66\u4E60\u4E24\u95E8\u8BFE\u7A0B,\u53EF\u4EE5\u76F8\u8F85\u76F8\u6210,\u67E5\u6F0F\u8865\u7F3A,\u4ECE\u6BD4\u8F83\u9274\u522B\u4E2D\u83B7\u5F97\u5B9E\u8D28\u7684\u8FDB\u6B65\u3002</p><p>\u200B \u7EFC\u4E0A\uFF0C\u4E3A\u4E86\u9002\u5E94\u5F53\u524D\u7D20\u8D28\u6559\u80B2\u7684\u8981\u6C42\uFF0C\u6539\u9769\u8BFE\u5802\u6559\u5B66\uFF0C\u5E94\u63D0\u5021\u6CE8\u91CD\u77E5\u8BC6\u70B9\u95F4\u7684\u5185\u5728\u8054\u7CFB\uFF0C\u5F3A\u8C03\u77E5\u8BC6\u7EFC\u5408\u3002\u5728\u6559\u5B66\u4E2D\uFF0C\u6559\u5E08\u9664\u4E86\u8981\u52A0\u5F3A\u57FA\u7840\u77E5\u8BC6\u6559\u5B66\u5916\uFF0C\u8FD8\u5E94\u52AA\u529B\u5E2E\u52A9\u5B66\u751F\u628A\u63E1\u77E5\u8BC6\u70B9\u4E4B\u95F4\u7684\u8054\u7CFB\uFF0C\u5F15\u5BFC\u5B66\u751F\u4ECE\u4E0D\u540C\u5C42\u9762\u3001\u4E0D\u540C\u89D2\u5EA6\u5206\u6790\u548C\u89E3\u51B3\u95EE\u9898\uFF0C\u63D0\u9AD8\u5B66\u751F\u7EFC\u5408\u8FD0\u7528\u77E5\u8BC6\u7684\u80FD\u529B\u3002\u540C\u65F6\u5E94\u6CE8\u91CD\u5B66\u751F\u7684\u4E3B\u4F53\u53C2\u4E0E\uFF0C\u9F13\u52B1\u521B\u65B0\u601D\u7EF4\u3002\u6559\u5E08\u8981\u5728\u5E73\u65F6\u7684\u6559\u5B66\u4E2D\uFF0C\u8981\u6CE8\u610F\u6316\u6398\u5B66\u751F\u7684\u65B0\u601D\u60F3\u3001\u65B0\u89C2\u70B9\uFF0C\u62D3\u5BBD\u5B66\u751F\u601D\u8DEF\u3002\u8981\u52AA\u529B\u63A2\u7D22\u65B0\u65B9\u6CD5\u3001\u65B0\u624B\u6BB5\uFF0C\u4F7F\u5B66\u751F\u5728\u751F\u52A8\u3001\u6D3B\u6CFC\u3001\u6709\u8DA3\u7684\u6C1B\u56F4\u91CC\u63A5\u53D7\u6559\u80B2\uFF0C\u771F\u6B63\u8C03\u52A8\u548C\u6FC0\u53D1\u5B66\u751F\u5BF9\u5B66\u4E60\u7684\u4E3B\u52A8\u6027\u548C\u79EF\u6781\u6027\uFF0C\u4ECE\u800C\u628A\u8BFE\u5802\u6559\u5B66\u63D0\u9AD8\u5230\u4E00\u4E2A\u65B0\u6C34\u5E73\u3002</p>',72),o=[r];function h(n,i){return e(),s("div",null,o)}var c=p(t,[["render",h],["__file","\u6D45\u8C08\u6587\u8A00\u6587\u4E0E\u82F1\u8BED\u8BED\u6CD5\u7684\u76F8\u4F3C\u70B9.html.vue"]]);export{c as default};
